    <Grid item xs={12} md={12} >
      <Paper elevation={0} className={classes.sidebarAboutBox}>
        <Typography align="center" variant="h6" gutterBottom>
          {title}
        </Typography>
        <Typography>{description}</Typography>
      </Paper>

